element.style:
#states > * {
  margin: 1px;
}
bar-card-name,
bar-card-value {
  font-size: 0.9rem;
  color: #ffffffaa;
  font-weight: bold;
}
bar-card-value	{
  font-weight: bolder;
}
bar-card-indicator {
  margin-top: 4px;
  transform: scaleY(-1);
}

card.style:
bar-card-currentbar, bar-card-current, bar-card-backgroundbar {

    height: 5px !important;
    margin-top: 30px;
}
ha-card {
    background: none;
    box-shadow: none;
}
ha-icon{
  color: white !important;
}
bar-card-iconbar {
    margin-bottom: 10px;
}
